package com.bridgelab.functions;
import java.util.Scanner;
public class BinarySearchString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s= new Scanner(System.in);
		System.out.println(" Binary Search For String array");
		
		String [] inputarray= utility.arrayinputString();
		System.out.println("\n inputrd array is");
		utility.printstringarray(inputarray);//to print array
		
		System.out.println("\n Enter the string for search");
		String search= s.nextLine();
		
		int result =utility.binarysearchstring(inputarray, search);
		
		if(result== -1)
		{
			System.out.println("\n Search String not found");
			
		}
		else
		{
			System.out.println("\n Search String  found");
			
		}
		
	}

}
